const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');
require('dotenv').config();

const app = express();

// Middleware
app.use(cors());
app.use(express.json());

// Routes
app.use('/api/auth', require('./routes/auth'));

// Basic route
app.get('/api', (req, res) => {
  res.json({ 
    message: 'GameVerse API is running!',
    version: '1.0.0',
    endpoints: {
      auth: '/api/auth'
    }
  });
});

// MongoDB connection with in-memory fallback for local dev
const startServer = (port) => {
  app.listen(port, () => {
    console.log(`üöÄ Server is running on http://localhost:${port}`);
  });
};

const connectWithFallback = async () => {
  const MONGODB_URI = process.env.MONGODB_URI;
  if (MONGODB_URI) {
    try {
      await mongoose.connect(MONGODB_URI);
      console.log('‚úÖ MongoDB connected successfully (MONGODB_URI)');
      startServer(process.env.PORT || 5000);
      return;
    } catch (err) {
      console.log('‚ùå MongoDB connection error with MONGODB_URI:', err);
    }
  }

  // Fallback to mongodb-memory-server for local development
  try {
    const { MongoMemoryServer } = require('mongodb-memory-server');
    const mongod = await MongoMemoryServer.create();
    const uri = mongod.getUri();
    await mongoose.connect(uri);
    console.log('‚úÖ MongoDB connected using in-memory server');
    startServer(process.env.PORT || 5000);
  } catch (err) {
    console.log('‚ùå MongoDB in-memory fallback failed:', err);
    process.exit(1);
  }
};

connectWithFallback();